import React, { Component } from "react";
import { Button } from 'react-bootstrap';
import { Modal } from 'react-bootstrap';

export default class SendMessage extends Component {
    state = {
        show: false,
        message: '',
        contacto: this.props.contacto
    }

    enviarMensaje = () => {
        const accountSid = 'ACa0e61b7d7b4b83d80017fe45a2ef5d93';
        const authToken = '774230e5a03928eb0f8f6cda1160b471';
        const client = require('twilio')(accountSid, authToken);

        client.messages
            .create({
                body: this.state.message,
                from: 'whatsapp:+14155238886',
                to: 'whatsapp:' + this.props.contacto.telefono
            })
            .then(message => console.log(message.sid))
            .done();
    }

    HandleClose = () => this.setState({ show: false });
    HandleShow = () => this.setState({ show: true });
    render() {
        return (
            <div>
                <Button variant="success" onClick={this.HandleShow}>
                    WhatsApp
                </Button>
                <Modal show={this.state.show} onHide={this.HandleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Envia un WhatsApp a {this.props.contacto.nombre}</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.HandleClose}>
                            Cancelar
                        </Button>
                        <Button variant="success" onClick={this.HandleClose}>
                            Enviar
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        )
    }
}
