import React, { Component } from "react";
import { Button } from 'react-bootstrap';
import { Modal } from 'react-bootstrap';

export default class SendMessage extends Component {
    state = {
        show: false,
    }
    HandleClose = () => this.setState({ message_show: false });
    createHandleClose = () => this.setState({ create_show: false });
    HandleShow = () => this.setState({ message_show: true });
    createHandleShow = () => this.setState({ create_show: true });
    render() {
        return (
            <div>
                <Button variant="primary" onClick={this.messageHandleShow}>
                    Launch demo modal
                </Button>
                <Modal show={this.state.message_show} onHide={this.messageHandleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Woohoo, you are reading this text in a modal!</Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.messageHandleClose}>
                            Close
                        </Button>
                        <Button variant="primary" onClick={this.messageHandleClose}>
                            Save Changes
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>
        )
    }
}
