import React, { Component } from "react";
import { Form, Container, Button, Accordion } from "react-bootstrap";
import axios from "axios";
import './Lugares.css'
export default class Lugares extends Component {
    state = {
        placeholder: 'Buscar por algún filtro',
        selectedOption: '',
        input: '',
        lugares: [],
    }

    handleRadioChange = (option) => {
        this.setState({ selectedOption: option });
    };

    onChangeName = (e) =>{
        this.setState(
            {input: e.target.value}
        )
    }

    handleButtonClick = async () => {
        let option = this.state.selectedOption
        if (option == 'Pais') {
                
        } else if (option == 'Nombre') {
            const res = await axios.get(process.env.REACT_APP_HOSTNAME + "/lugares/" + this.state.input)
            console.log(res.data)
        } else if (option == 'Categoria') {

        } else {
            alert("Debes elegir una opción de busqueda")
        }
    };

    render() {
        return (
            <div className="d-flex flex-column align-items-center justify-content-center vh-100">
                <h1>Bienvenido a la aplicación del Turista</h1>
                <p>¡Bienvenido a nuestra aplicación de búsqueda de lugares! Descubre nuevos destinos de una manera sencilla y rápida utilizando nuestros tres filtros intuitivos.</p>
                <p>Solo tienes que elegir el filtro que se adapte a tu búsqueda, escribir tu consulta y hacer clic en buscar. ¡Prepárate para una emocionante aventura llena de descubrimientos!</p>
                <Container>
                    <Form className="text-center">
                        <Form.Control type="text" id="inputText1" placeholder={this.state.placeholder} onChange={this.onChangeName} className="text-center mb-3" />

                        <div className="d-flex justify-content-between mb-3">
                            <Form.Check
                                type="radio"
                                label="Buscar por País"
                                name="radioGroup"
                                id="opcion1"
                                className="mr-3"
                                onChange={() => this.handleRadioChange('Pais')}
                            />
                            <Form.Check
                                type="radio"
                                label="Buscar por Nombre"
                                name="radioGroup"
                                id="opcion2"
                                className="mr-3"
                                onChange={() => this.handleRadioChange('Nombre')}
                            />
                            <Form.Check
                                type="radio"
                                label="Buscar por Categoría"
                                name="radioGroup"
                                id="opcion3"
                                className="mr-3"
                                onChange={() => this.handleRadioChange('Categoria')}
                            />
                        </div>

                        <Button className="w-100 mb-4" onClick={this.handleButtonClick}>Buscar</Button>
                    </Form>
                    <Accordion>
                        <Accordion.Item eventKey="0">
                            <Accordion.Header>Accordion Item #1</Accordion.Header>
                            <Accordion.Body>
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
                                eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
                                minim veniam, quis nostrud exercitation ullamco laboris nisi ut
                                aliquip ex ea commodo consequat. Duis aute irure dolor in
                                reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
                                pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
                                culpa qui officia deserunt mollit anim id est laborum.
                            </Accordion.Body>
                        </Accordion.Item>
                        <Accordion.Item eventKey="1">
                            <Accordion.Header>Accordion Item #2</Accordion.Header>
                            <Accordion.Body>
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
                                eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
                                minim veniam, quis nostrud exercitation ullamco laboris nisi ut
                                aliquip ex ea commodo consequat. Duis aute irure dolor in
                                reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
                                pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
                                culpa qui officia deserunt mollit anim id est laborum.
                            </Accordion.Body>
                        </Accordion.Item>
                    </Accordion>
                </Container>
            </div>
        )
    }
}
