import React, { Component } from "react";
import {Button} from 'react-bootstrap';
import {Modal} from 'react-bootstrap';

export default class SendMessage extends Component {
    state = {
        message_show: false,
        create_show: false
    }
    messageHandleClose = () => this.setState({message_show: false});
    createHandleClose = () => this.setState({create_show: false});
    messageHandleShow = () => this.setState({message_show: true});
    createHandleShow = () => this.setState({create_show: true});
    render() {
        return (
        <Modal show={this.props.message_show} onHide={this.props.messageHandleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Modal heading</Modal.Title>
            </Modal.Header>
            <Modal.Body>Woohoo, you are reading this text in a modal!</Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={this.props.messageHandleClose}>
                Close
              </Button>
              <Button variant="primary" onClick={this.props.messageHandleClose}>
                Save Changes
              </Button>
            </Modal.Footer>
          </Modal>
            )
    }
}
